// 로그인이 되어 있는지 안되어 있는지 체크
///////////////////////////////////        
this.getUserProfile = function () {
	var vm = this;
	$.ajax({
		url: ioRootPath + "/auth/user.do",
		crossDomain: true,
		dataType: 'jsonp'
	})
		.success(function (res) {
			console.log('signin', res)
			var code = res.ResultCode[0].Code;
			if (code == "10000") {
				root.isSignUp = true;
				root.UIP = res.ResultCode[0].UserIP;
				root.getGachaInfoDataInit();
				////
			} else {
				root.isSignUp = false;
				//화면초기화
				root.initViewPort();
				//초기 메인띄우기
				root.screenViewControl(1);
				//signIn
				root.checkMainStatus(0);
			}
		})
		.fail(function () {
			console.log("error");
		});
}
