this.getGachaInfoDataInit = function () {
	$.ajax({
		url: ioRootPath + "/secured/json.do?callback=?",
		type: 'GET',
		data: {
			cm: "Service.Gacha.GetGachaUserInfo_DK",
			serviceid: 2,
			language: 'en'
		},
		crossDomain: true,
		dataType: 'json'
	})
		.success(function (res) {
			SEASON_TITLE = "";
			GOT_KEY = 0;
			isBoxKeyNo0 = true;
			isBoxKeyNo1 = false;
			isBoxKeyNo2 = false;
			ChestBoxSelectNo = 1;
			KEY_PRICE = 0;
			MY_TOTAL_BALANCE = 0;
			DISCOUNT_05 = 0;
			DISCOUNT_10 = 0;
			GachaUserInfo = [];
			GachaSeasonInfo = [];
			SEVICE_STATUS = 0
			USER_UNID = 0
			var code = res.ResultCode[0].Code;
			console.log('getGachaInfoDataInit', res)
			if (code === "10000") {
				//
				GachaUserInfo = res.GachaUserInfo;
				GachaSeasonInfo = res.GachaSeasonInfo;
				//
				GOT_KEY = res.GachaUserInfo[0].KeyAmount;
				MY_TOTAL_BALANCE = res.GachaUserInfo[0].UWCBalance;
				KEY_PRICE = res.KEY_Price[0].KeyPrice;
				//
				DISCOUNT_01 = res.KEY_Price[0].Keyx1;
				DISCOUNT_05 = res.KEY_Price[0].Keyx5;
				DISCOUNT_10 = res.KEY_Price[0].Keyx10;
				SEASON_TITLE = GachaSeasonInfo[0].SeasonName;
				//
				SEVICE_STATUS = res.MaintenanceSchdule[0].ServiceStatus;
				//SEVICE_STATUS = 0;
				USER_UNID = res.GachaUserInfo[0].UNID;
				//
				if (KEY_PRICE > 1) {
					countArr[0] = {
						value: 1,
						discount: 0,
						price: KEY_PRICE
					};
					countArr[1] = {
						price: KEY_PRICE - (KEY_PRICE * (DISCOUNT_05 / 100)),
						value: 5,
						discount: DISCOUNT_05,
					};
					countArr[2] = {
						price: KEY_PRICE - (KEY_PRICE * (DISCOUNT_10 / 100)),
						value: 10,
						discount: DISCOUNT_10,
					};
					countArr[3] = {
						price: KEY_PRICE - (KEY_PRICE * (DISCOUNT_10 / 100)),
						value: 'CHOOSE',
						discount: DISCOUNT_10,
					};
				}
				//check maintenance	
				if (SEVICE_STATUS == 1) {
					if (USER_UNID == 3675752 || USER_UNID == 3818901 || USER_UNID == 3856766 || USER_UNID == 4605726) {
						//화면초기화
						root.initViewPort();
						//초기 메인띄우기
						root.screenViewControl(1);
						//gameStart
						root.checkMainStatus(2);
					} else {
						isStartEnable = "M";
						//화면초기화
						root.initViewPort();
						//초기 메인띄우기
						root.screenViewControl(1);
						//gameStart
						root.checkMainStatus(1);
					}
				} else {
					if (GachaSeasonInfo != '') {
						//화면초기화
						root.initViewPort();
						//초기 메인띄우기
						root.screenViewControl(1);
						//
						root.checkMainStatus(2);
					} else {
						isStartEnable = "M";
						//화면초기화
						root.initViewPort();
						//초기 메인띄우기
						root.screenViewControl(1);
						//gameStart
						root.checkMainStatus(1);
					}
				}
			} else {
				isStartEnable = "M";
				root.initViewPort();
				root.screenViewControl(1);
				root.checkMainStatus(1);

				if (code === "10001") {} else if (code === "10002") {} else if (code === "10003") {} else if (code === "90000") {}
			}
		})
		.error(function (e) {
			console.log("error");
		})
}
