this.initViewPort = function () {
	//mainPage Btn resets
	root.resetMainBtnHandler();
	//snb Menu Resets
	root.subMenuBarEventDisable();
	for (var i = 0; i < screenNameArr.length; i++) {
		root[screenNameArr[i].name].visible = false;
		root[screenNameArr[i].name].x = -1000;
		root[screenNameArr[i].name].y = -1000;
	}
}

this.screenViewControl = function (id) {
	switch (id) {
		case 0:
			root.initViewPort();
			break;
		case 1:
			root.subMenuControl("invisible");
			root[screenNameArr[0].name].visible = true;
			root[screenNameArr[0].name].x = screenNameArr[0].posx;
			root[screenNameArr[0].name].y = screenNameArr[0].posy;
			break;
		case 2:
			root.subMenuControl("visible");
			root[screenNameArr[1].name].visible = true;
			root[screenNameArr[1].name].x = screenNameArr[1].posx;
			root[screenNameArr[1].name].y = screenNameArr[1].posy;
			//
			root.checkGameStatus();
			break;
		case 3:
			//buy-key
			root.subMenuBarEventDisable();
			root.mc_game_menu_bar.gotoAndPlay('end');
			root.subMenuControl("partVisible");
			root[screenNameArr[2].name].visible = true;
			root[screenNameArr[2].name].x = screenNameArr[2].posx;
			root[screenNameArr[2].name].y = screenNameArr[2].posy;
			//
			root.checkBuyKeyStatus();
			//
			break;
		case 4:
			//sc_inventory
			root.subMenuBarEventDisable();
			root.mc_game_menu_bar.gotoAndPlay('end');
			root.subMenuControl("partVisible");
			root[screenNameArr[3].name].visible = true;
			root[screenNameArr[3].name].x = screenNameArr[3].posx;
			root[screenNameArr[3].name].y = screenNameArr[3].posy;
			//	    
			root.checkInvenStatus();
			//
			break;
		case 5:
			//sc_item_table
			root.subMenuBarEventDisable();
			root.mc_game_menu_bar.gotoAndPlay('end');
			this.subMenuControl("partVisible");
			root[screenNameArr[4].name].visible = true;
			root[screenNameArr[4].name].x = screenNameArr[4].posx;
			root[screenNameArr[4].name].y = screenNameArr[4].posy;
			//	    
			root.checkItemTableStatus();
			break;
		case 6:
			//sc_recycle
			//sc_item_table
			root.subMenuBarEventDisable();
			root.mc_game_menu_bar.gotoAndPlay('end');
			root.subMenuControl("invisible");
			root[screenNameArr[5].name].visible = true;
			root[screenNameArr[5].name].x = screenNameArr[5].posx;
			root[screenNameArr[5].name].y = screenNameArr[5].posy;
			//	    
			root.checkRecycleStatus();
			break;
	}
}
//
this.subMenuControl = function (id) {
	switch (id) {
		case "invisible":
			for (var i = 0; i < subNameArr.length; i++) {
				root[subNameArr[i].name].visible = false
				root[subNameArr[i].name].x = -1000;
				root[subNameArr[i].name].y = -1000;
				console.log('invisible', root[subNameArr[i].name], root[subNameArr[i].name].y)
			}
			break;
		case "visible":
			for (var i = 0; i < subNameArr.length; i++) {
				root[subNameArr[i].name].visible = true;
				root[subNameArr[i].name].x = subNameArr[i].posx;
				root[subNameArr[i].name].y = subNameArr[i].posy;
			}
			root.subMenuBarEventEnable();
			break;
		case "partVisible":
			for (var i = 0; i < subNameArr.length; i++) {
				if (i == 2) {
					root[subNameArr[i].name].visible = false
					root[subNameArr[i].name].x = -1000;
					root[subNameArr[i].name].y = -1000;
				} else {
					root[subNameArr[i].name].visible = true;
					root[subNameArr[i].name].x = subNameArr[i].posx;
					root[subNameArr[i].name].y = subNameArr[i].posy;
				}
			}
			root.subMenuBarEventEnable();
			break;
	}
}
