this.subMenuBarEventEnable = function () {
	var mcGameMenuBar = root.mc_game_menu_bar.mc_menubar_ani
	var btnInven = mcGameMenuBar.btn_inven
	var btnTable = mcGameMenuBar.btn_item_table
	var btnGetKey = mcGameMenuBar.btn_get_key
	var btnHome = mcGameMenuBar.btn_home
	var btnGame = mcGameMenuBar.mc_title

	var hit = new createjs.Shape();
	hit.graphics.beginFill("#000").drawRect(3, 17, 268, 40);
	btnGame.hitArea = hit;
	btnGame.cursor = "pointer";

	var txtSeasonTitle = mcGameMenuBar.season_title
	txtSeasonTitle.text = SEASON_TITLE;
	//
	if (GOT_KEY === 0) {
		btnGetKey.txt.text = "GET KEY";
	} else {
		btnGetKey.txt.text = GOT_KEY;
	}
	//
	btnHome.addEventListener("click", function (event) {
		root.getGachaInfoDataReset();
		if (isStartEnable === "M") {
			if (USER_UNID == 3675752 || USER_UNID == 3818901 || USER_UNID == 3856766 || USER_UNID == 4605726) {
				root.initViewPort();
				root.screenViewControl(1);
				root.checkMainStatus(2);
			} else {
				root.initViewPort();
				root.screenViewControl(1);
				root.checkMainStatus(1);
			}
		} else {
			root.initViewPort();
			root.screenViewControl(1);
			root.checkMainStatus(2);
		}
		event.currentTarget.removeAllEventListeners();
	});
	//
	btnGame.addEventListener("click", function (event) {
		root.goBackCheck(2);
		event.currentTarget.removeAllEventListeners();
	});
	//
	btnGetKey.addEventListener("click", function (event) {
		root.goBackCheck(3);
		event.currentTarget.removeAllEventListeners();
	});
	//
	btnInven.addEventListener("click", function (event) {
		root.goBackCheck(4);
		event.currentTarget.removeAllEventListeners();
	});
	//
	btnTable.addEventListener("click", function (event) {
		root.goBackCheck(5);
		event.currentTarget.removeAllEventListeners();
	});
}

this.subMenuBarEventDisable = function () {
	var mcGameMenuBar = root.mc_game_menu_bar.mc_menubar_ani
	var btnInven = mcGameMenuBar.btn_inven;
	var btnTable = mcGameMenuBar.btn_item_table;
	var btnGetKey = mcGameMenuBar.btn_get_key;
	var btnHome = mcGameMenuBar.btn_home
	var btnGame = mcGameMenuBar.mc_title
	btnInven.removeAllEventListeners();
	btnTable.removeAllEventListeners();
	btnGetKey.removeAllEventListeners();
	btnGame.removeAllEventListeners();
	btnHome.removeAllEventListeners();
}
this.goBackCheck = function (id) {
	root.getGachaInfoDataReset();
	if (isStartEnable === "M") {
		if (USER_UNID == 3675752 || USER_UNID == 3818901 || USER_UNID == 3856766 || USER_UNID == 4605726) {
			root.initViewPort();
			root.screenViewControl(id);
		} else {
			root.initViewPort();
			root.screenViewControl(1);
			root.checkMainStatus(1);
		}
	} else {
		root.initViewPort();
		root.screenViewControl(id);
	}
}
root.getUserProfile();

