//0:signIn 1:maintenance 2:start
this.checkMainStatus = function (id) {
	this.initMainStat();
	//sound btn enable
	this.bgSoundEventsEnable();
	//this.sc_main.mc_main_btns.mc_sound.removeAllEventListeners();	
	this.howToPlayEventsEnable();
	//stat
	if (id === 0) {
		this.statSignIn();
	}
	if (id === 1) {
		this.statMaintenance();
	}
	if (id === 2) {
		this.statStart();
	}
}

this.initMainStat = function () {
	var mcBlur = this.sc_main.mc_main_blur_bg;
	var mcMaintenance = this.sc_main.mc_main_maintenance;
	var mcSign = this.sc_main.mc_main_signin;
	var mcMainBg = this.sc_main.mc_main_bg;
	var mcMainStart = this.sc_main.mc_main_start;

	mcBlur.visible = false;
	mcBlur.x = -1000;
	mcBlur.y = -1000;

	mcMaintenance.gotoAndPlay(0);
	mcMaintenance.x = -1000;
	mcMaintenance.y = -1000;

	mcSign.gotoAndPlay(0);
	mcSign.x = -1000;
	mcSign.y = -1000;

	mcMainBg.gotoAndPlay(0);
	mcMainBg.x = -1000;
	mcMainBg.y = -1000;

	mcMainStart.gotoAndPlay(0);
	mcMainStart.x = -1000;
	mcMainStart.y = -1000;
	//
}

this.statMaintenance = function () {
	var mcBlur = this.sc_main.mc_main_blur_bg;
	var mcMaintenance = this.sc_main.mc_main_maintenance;
	//	
	mcBlur.visible = true;
	mcBlur.x = 5;
	mcBlur.y = 2;
	//
	mcMaintenance.x = 244;
	mcMaintenance.y = 215;
	mcMaintenance.gotoAndPlay("start");
	//
}
this.statSignIn = function () {
	var mcBlur = root.sc_main.mc_main_blur_bg;
	var mcSign = root.sc_main.mc_main_signin;
	//
	mcBlur.visible = true;
	mcBlur.x = 5;
	mcBlur.y = 2;
	//
	mcSign.x = 244;
	mcSign.y = 126;
	mcSign.gotoAndPlay('start');
	//
	mcSign.mc_signin_ani.btn_sign_in.addEventListener("click", function (event) {
		location.href = "https://www.papayaplay.com/account/signin.do?redirect_url=https://uwo.papayaplay.com/treasurecove/main.do";
	});
}

this.statStart = function () {
	var mcMainBg = root.sc_main.mc_main_bg;
	var mcMainStart = root.sc_main.mc_main_start;
	//
	mcMainBg.x = 2.55;
	mcMainBg.y = 3;
	mcMainBg.gotoAndPlay('start');
	//
	mcMainStart.x = 293;
	mcMainStart.y = 130;
	mcMainStart.gotoAndPlay('start');
	//
	mcMainStart.btn_start.addEventListener("click", function (event) {
		//화면 초기화
		root.initViewPort();
		//게임화면
		root.screenViewControl(2);
	});
}


this.bgSoundEventsEnable = function () {
	root.isBgSoundOn = true;
	var btnSound = this.sc_main.mc_main_btns.mc_sound
	btnSound.gotoAndPlay("on");

	var hit = new createjs.Shape();
	hit.graphics.beginFill("#000").drawRect(0, 0, 30, 30);

	btnSound.hitArea = hit;
	btnSound.cursor = "pointer";

	btnSound.addEventListener("mouseover", function (event) {
		if (root.isBgSoundOn) {
			event.currentTarget.gotoAndPlay("off");
		} else {
			event.currentTarget.gotoAndPlay("on");
		}
	});

	btnSound.addEventListener("mouseout", function (event) {
		if (root.isBgSoundOn) {
			event.currentTarget.gotoAndPlay("on");
		} else {
			event.currentTarget.gotoAndPlay("off");
		}
	});

	btnSound.addEventListener("click", function (event) {
		root.isBgSoundOn = !root.isBgSoundOn
		if (root.isBgSoundOn) {
			event.currentTarget.gotoAndPlay("off");
		} else {
			event.currentTarget.gotoAndPlay("on");
		}
	});
}

this.howToPlayEventsEnable = function () {
	this.sc_main.mc_main_btns.btn_howto.addEventListener("click", function (event) {
		location.href = "";
	});
}

this.resetMainBtnHandler = function () {
	root.mc_select_crystal_bar.gotoAndPlay(0);
	var btnSignIn = root.sc_main.mc_main_signin.mc_signin_ani.btn_sign_in;
	var btnStart = root.sc_main.mc_main_start.btn_start;
	var btnSound = root.sc_main.mc_main_btns.mc_sound;
	var btnHowTo = root.sc_main.mc_main_btns.btn_howto;
	btnSignIn.removeAllEventListeners();
	btnStart.removeAllEventListeners();
	btnSound.removeAllEventListeners();
	btnHowTo.removeAllEventListeners();
}

