this.checkGameStatus = function () {
	root.mc_game_menu_bar.gotoAndPlay('start');
	//1,3,5 라디오 버튼은 무비클립에서 호출 (sc_game.frame(start));
	root.sc_game.gotoAndPlay('start');
	//크리스탈 호출
	setTimeout(function (e) {
		if (GOT_KEY > 0) {
			root.setRBBoxKey(0);
			root.setKrystalNo(1);
		} else {
			root.setKrystalNo(0)
		}
		root.mc_select_crystal_bar.gotoAndPlay('start');
		root.subSelectBarEventEnable();
	}, 500);
}
