this.setRBBoxKey = function (p) {
	var mc0 = root.mc_select_crystal_bar.mc_select_crystal_ani.rb_box_no0;
	var mc1 = root.mc_select_crystal_bar.mc_select_crystal_ani.rb_box_no1;
	var mc2 = root.mc_select_crystal_bar.mc_select_crystal_ani.rb_box_no2;

	if (p === 0) {
		isBoxKeyNo0 = true;
		isBoxKeyNo1 = false;
		isBoxKeyNo2 = false;
		ChestBoxSelectNo = 1;
	}
	if (p === 1) {
		isBoxKeyNo0 = false;
		isBoxKeyNo1 = true;
		isBoxKeyNo2 = false;
		ChestBoxSelectNo = 3;
	}
	if (p === 2) {
		isBoxKeyNo0 = false;
		isBoxKeyNo1 = false;
		isBoxKeyNo2 = true;
		ChestBoxSelectNo = 5;
	}

	if (isBoxKeyNo0) {
		mc0.gotoAndPlay("selected");
		mc0.no_text.color = "#F0B319"
	} else {
		root.setBtnColorChange(0);
	}

	if (isBoxKeyNo1) {
		mc1.gotoAndPlay("selected");
		mc1.no_text.color = "#F0B319"
	} else {
		root.setBtnColorChange(1);
	}

	if (isBoxKeyNo2) {
		mc2.gotoAndPlay("selected");
		mc2.no_text.color = "#F0B319"
	} else {
		root.setBtnColorChange(2);
	}

	//박스세팅
	//setChestBoxInit(ChestBoxSelectNo);
}
this.setBtnColorChange = function (i) {
	var mc0 = root.mc_select_crystal_bar.mc_select_crystal_ani.rb_box_no0;
	var mc1 = root.mc_select_crystal_bar.mc_select_crystal_ani.rb_box_no1;
	var mc2 = root.mc_select_crystal_bar.mc_select_crystal_ani.rb_box_no2;
	if (GOT_KEY <= 0) {
		eval('mc' + i).gotoAndPlay("disable");
		eval('mc' + i).no_text.color = "#999999";
	} else if (GOT_KEY >= 1 && GOT_KEY <= 2) {
		if (i !== 0) {
			eval('mc' + i).gotoAndPlay("disable");
			eval('mc' + i).no_text.color = "#999999";
		} else {
			eval('mc' + i).gotoAndPlay("normal");
			eval('mc' + i).no_text.color = "#fff"
		}
	} else if (GOT_KEY >= 3 && GOT_KEY <= 4) {
		if (i === 2) {
			eval('mc' + i).gotoAndPlay("disable");
			eval('mc' + i).no_text.color = "#999999";
		} else {
			eval('mc' + i).gotoAndPlay("normal");
			eval('mc' + i).no_text.color = "#fff"
		}
	} else if (GOT_KEY >= 5) {
		eval('mc' + i).gotoAndPlay("normal");
		eval('mc' + i).no_text.color = "#fff"
	}
}
this.setKrystalNo = function (no) {
	var mc = root.sc_game
	var error_window = mc.mc_error_buy_key;
	var btn_buy_key = mc.btn_go_buy_key;
	btn_buy_key.removeAllEventListeners();
	if (no == 0) {
		mc.gotoAndPlay("open_krystal0");
		//error_window
		//키상점 가기
		btn_buy_key.addEventListener("click", function (event) {
			root.goBackCheck(3);
		});
	}
	if (no == 1) {
		mc.gotoAndPlay("open_krystal1");
		root.setBreakKrystal();
	}
	if (no == 2) {
		mc.gotoAndPlay("open_krystal3");
		root.setBreakKrystal();
	}
	if (no == 3) {
		mc.gotoAndPlay("open_krystal5");
		root.setBreakKrystal();
	}
}

