}

this.subSelectBarEventEnable = function () {
	var mc0 = root.mc_select_crystal_bar.mc_select_crystal_ani.rb_box_no0;
	var mc1 = root.mc_select_crystal_bar.mc_select_crystal_ani.rb_box_no1;
	var mc2 = root.mc_select_crystal_bar.mc_select_crystal_ani.rb_box_no2;

	mc0.no_text.text = '1';
	mc1.no_text.text = '3';
	mc2.no_text.text = '5';

	var hit = new createjs.Shape();
	hit.graphics.beginFill("#000").drawRect(0, 0, 34, 20);

	for (var i = 0; i < 3; i++) {
		eval('mc' + i).removeAllEventListeners();
		if (GOT_KEY <= 0) {
			eval('mc' + i).gotoAndPlay("disable");
			eval('mc' + i).no_text.color = "#999999";
			eval('mc' + i).removeAllEventListeners();
		} else if (GOT_KEY >= 1 && GOT_KEY <= 2) {
			if (i !== 0) {
				eval('mc' + i).gotoAndPlay("disable");
				eval('mc' + i).no_text.color = "#999999";
				eval('mc' + i).removeAllEventListeners();
			} else {
				eval('mc' + i).hitArea = hit;
				eval('mc' + i).cursor = "pointer";
				eval('mc' + i).addEventListener("mouseover", function (event) {
					var id = Number(event.currentTarget.name.substring(9));
					if (eval('isBoxKeyNo' + id)) {
						event.currentTarget.no_text.color = "#fff";
						event.currentTarget.gotoAndPlay("selectOver")
					} else {
						event.currentTarget.no_text.color = "#F0B319";
						event.currentTarget.gotoAndPlay("over")
					}
				});
				eval('mc' + i).addEventListener("mouseout", function (event) {
					var id = Number(event.currentTarget.name.substring(9));
					if (eval('isBoxKeyNo' + id)) {
						event.currentTarget.no_text.color = "#F0B319";
						event.currentTarget.gotoAndPlay("selectOut")
					} else {
						event.currentTarget.no_text.color = "#fff";
						event.currentTarget.gotoAndPlay("normal")
					}
				});
				eval('mc' + i).addEventListener("click", function (event) {
					var id = Number(event.currentTarget.name.substring(9));
					root.setRBBoxKey(id);
					//
					root.setKrystalNo(id + 1);
				});
			}
		} else if (GOT_KEY >= 3 && GOT_KEY <= 4) {
			if (i === 2) {
				eval('mc' + i).gotoAndPlay("disable");
				eval('mc' + i).no_text.color = "#999999";
				eval('mc' + i).removeAllEventListeners();
			} else {
				eval('mc' + i).hitArea = hit;
				eval('mc' + i).cursor = "pointer";
				eval('mc' + i).addEventListener("mouseover", function (event) {
					var id = Number(event.currentTarget.name.substring(9));
					if (eval('isBoxKeyNo' + id)) {
						event.currentTarget.no_text.color = "#fff";
						event.currentTarget.gotoAndPlay("selectOver")
					} else {
						event.currentTarget.no_text.color = "#F0B319";
						event.currentTarget.gotoAndPlay("over")
					}
				});
				eval('mc' + i).addEventListener("mouseout", function (event) {
					var id = Number(event.currentTarget.name.substring(9));
					if (eval('isBoxKeyNo' + id)) {
						event.currentTarget.no_text.color = "#F0B319";
						event.currentTarget.gotoAndPlay("selectOut")
					} else {
						event.currentTarget.no_text.color = "#fff";
						event.currentTarget.gotoAndPlay("normal")
					}
				});
				eval('mc' + i).addEventListener("click", function (event) {
					var id = Number(event.currentTarget.name.substring(9));
					root.setRBBoxKey(id);
					//
					root.setKrystalNo(id + 1);
				});
			}
		} else if (GOT_KEY >= 5) {
			eval('mc' + i).hitArea = hit;
			eval('mc' + i).cursor = "pointer";
			eval('mc' + i).addEventListener("mouseover", function (event) {
				var id = Number(event.currentTarget.name.substring(9));
				if (eval('isBoxKeyNo' + id)) {
					event.currentTarget.no_text.color = "#fff";
					event.currentTarget.gotoAndPlay("selectOver")
				} else {
					event.currentTarget.no_text.color = "#F0B319";
					event.currentTarget.gotoAndPlay("over")
				}
			});
			eval('mc' + i).addEventListener("mouseout", function (event) {
				var id = Number(event.currentTarget.name.substring(9));
				if (eval('isBoxKeyNo' + id)) {
					event.currentTarget.no_text.color = "#F0B319";
					event.currentTarget.gotoAndPlay("selectOut")
				} else {
					event.currentTarget.no_text.color = "#fff";
					event.currentTarget.gotoAndPlay("normal")
				}
			});
			eval('mc' + i).addEventListener("click", function (event) {
				var id = Number(event.currentTarget.name.substring(9));
				root.setRBBoxKey(id);
				//
				root.setKrystalNo(id + 1);
			});
		}
	}
}
this.setBreakKrystal = function () {
	var mc = root.sc_game
	var hit = new createjs.Shape();
	hit.graphics.beginFill("#000").drawRect(22, 23, 98, 197);
	for (var i = 0; i < ChestBoxSelectNo; i++) {
		mc['mc_krystal' + i].removeAllEventListeners();

		mc['mc_krystal' + i].hitArea = hit;
		mc['mc_krystal' + i].cursor = "pointer";
		mc['mc_krystal' + i].addEventListener("mouseover", function (event) {
			for (var a = 0; a < ChestBoxSelectNo; a++) {
				mc['mc_krystal' + a].gotoAndPlay('over');
				mc['mc_krystal' + a].mc_krystal_over.gotoAndPlay('start');
				mc['mc_krystal' + a].mc_krystal_over.mc_game_krystal_ani.gotoAndPlay('start');
			}
		});

		mc['mc_krystal' + i].addEventListener("mouseout", function (event) {
			for (var a = 0; a < ChestBoxSelectNo; a++) {
				mc['mc_krystal' + a].gotoAndPlay('start');
				mc['mc_krystal' + a].mc_krystal_over.gotoAndPlay(0);
				mc['mc_krystal' + a].mc_krystal_over.mc_game_krystal_ani.gotoAndPlay(0);
			}
		});

		mc['mc_krystal' + i].addEventListener("click", function (event) {
			for (var a = 0; a < ChestBoxSelectNo; a++) {
				mc['mc_krystal' + a].mc_krystal_over.gotoAndPlay(0);
				mc['mc_krystal' + a].mc_krystal_over.mc_game_krystal_ani.gotoAndPlay(0);
				mc['mc_krystal' + a].removeAllEventListeners();
			}
			var id = event.currentTarget.name.substring(10);
			getSingleResultItems(id)
			//상품디테일
		});
	}
}
