this.setMenuBlocker = function (p) {
	var mc = root.sc_block;
	var hit = new createjs.Shape();
	hit.graphics.beginFill("#000").drawRect(0, 0, 980, 200);
	mc.hitArea = hit;
	mc.addEventListener("click", function (event) {});
	if (p == 'on') {
		mc.x = 0;
		mc.y = 0;
	}
	if (p == 'off') {
		mc.x = 1000;
		mc.y = 1000;
		mc.removeAllEventListeners();
	}
}
this.setLoadingBar = function (p) {
	var mc = root.sc_loading
	mc.x = 0;
	mc.y = 0;
	if (p == 'on') {
		mc.gotoAndPlay('start');
	}
	if (p == 'off') {
		mc.gotoAndPlay('end');
		setTimeout(function () {
			mc.x = 1000;
			mc.y = 1000;
		}, 400)
	}
}
function getSingleResultItems(id) {
	items = [];
	// 상단 버튼 disabled
	root.setMenuBlocker('on');
	//로딩바
	root.setLoadingBar('on');

	$.ajax({
		url: ioRootPath + "/secured/json.do?callback=?",
		type: 'GET',
		data: {
			cm: "Service.Gacha.OpenGachaBox_DK",
			serviceid: 2,
			keyCount: ChestBoxSelectNo,
			userip: UIP,
		},
		crossDomain: true,
		dataType: 'json',
		success: function (data) {
			console.log(data);
			var code = data.ResultCode[0].Code;
			GachaUserInfo = [];
			if (code === "10000") {
				//로딩바
				root.setLoadingBar('off');
				//
				isOpenedChest = false;
				items = data.GetNewGachaItem;
				GachaUserInfo = data.GachaUserInfo;
				GOT_KEY = data.GachaUserInfo[0].KeyAmount;
				MY_TOTAL_BALANCE = data.GachaUserInfo[0].UWCBalance;

				if (GOT_KEY === 0) {
					root.mc_game_menu_bar.mc_menubar_ani.btn_get_key.txt.text = "GET KEY";
				} else {
					root.mc_game_menu_bar.mc_menubar_ani.btn_get_key.txt.text = GOT_KEY;
				}

				if (ChestBoxSelectNo == 1) {
					root.OpenChestItemLoad(items, id);
				} else {
					root.OpenChestAllItemLoad(items, ChestBoxSelectNo);
				}
			} else if (code === "10004") {
				root.setLoadingBar('off');
				// 상단 버튼 disabled
				root.setMenuBlocker('off');
				root.goBackCheck(2);
				//
			} else {
				root.setLoadingBar('off');
				// 상단 버튼 disabled
				root.setMenuBlocker('off');
				location.reload();
			}
		},
		error: function () {
			console.log("error");
		}
	});
}

this.OpenChestItemLoad = function (arr, id) {
	var iconBitmap = new createjs.Bitmap(arr[0].IconURL);
	var itemTier = (arr[0].Tier) - 1;
	var mc = root.sc_game['mc_krystal' + id]
	mc.mc_tier_bg.mc_icon_place.removeChildAt(0);
	mc.mc_tier_bg.mc_icon_place.addChild(iconBitmap);
	mc.mc_tier_bg.gotoAndStop(itemTier);
	mc.gotoAndPlay("open");
	mc.mc_light_bg['tier' + arr[0].Tier].gotoAndPlay('start');

	var timeDelay = NORMAL_TIMER;

	if (Number(items[0].Tier) === 1) {
		timeDelay = PLA_TIMER;
	} else if (Number(items[0].Tier) === 2) {
		timeDelay = GOLD_TIMER;
	} else {
		timeDelay = NORMAL_TIMER;
	}

	setTimeout(function () {
		root.openItemDetail(arr, ChestBoxSelectNo);
	}, timeDelay);
	//
}

this.OpenChestAllItemLoad = function (arr, id) {
	desc = [];
	if (id === 3) {
		for (var i = 0; i < 3; i++) {
			var iconBitmap = new createjs.Bitmap(arr[i].IconURL);
			var itemTier = (arr[i].Tier) - 1;
			console.log('timeDelay', arr[i].Tier)
			desc.push(arr[i].Tier);
			var mc = root.sc_game['mc_krystal' + i]
			mc.mc_tier_bg.mc_icon_place.removeChildAt(0);
			mc.mc_tier_bg.mc_icon_place.addChild(iconBitmap);
			mc.mc_tier_bg.gotoAndStop(itemTier);
			mc.gotoAndPlay("open");
			mc.mc_light_bg['tier' + arr[i].Tier].gotoAndPlay('start');
		}
		desc.sort(function (a, b) {
			return a - b
		});
		var timeDelay = NORMAL_TIMER;
		if (Number(desc[0].Tier) === 1) {
			timeDelay = PLA_TIMER;
		} else if (Number(desc[0].Tier) === 2) {
			timeDelay = GOLD_TIMER;
		} else {
			timeDelay = NORMAL_TIMER;
		}

		setTimeout(function () {
			root.openItemDetail(arr, ChestBoxSelectNo);
		}, timeDelay);
	}
	if (id === 5) {
		for (var i = 0; i < 5; i++) {
			var iconBitmap = new createjs.Bitmap(arr[i].IconURL);
			var itemTier = (arr[i].Tier) - 1;
			desc.push(arr[i].Tier);
			var mc = root.sc_game['mc_krystal' + i]
			mc.mc_tier_bg.mc_icon_place.removeChildAt(0);
			mc.mc_tier_bg.mc_icon_place.addChild(iconBitmap);
			mc.mc_tier_bg.gotoAndStop(itemTier);
			mc.gotoAndPlay("open");
			mc.mc_light_bg['tier' + arr[i].Tier].gotoAndPlay('start');
		}
		desc.sort(function (a, b) {
			return a - b
		});
		var timeDelay = NORMAL_TIMER;
		if (Number(desc[0].Tier) === 1) {
			timeDelay = PLA_TIMER;
		} else if (Number(desc[0].Tier) === 2) {
			timeDelay = GOLD_TIMER;
		} else {
			timeDelay = NORMAL_TIMER;
		}

		setTimeout(function () {
			root.openItemDetail(arr, ChestBoxSelectNo);
		}, timeDelay);
	}

	console.log('timeDelay', timeDelay)
	//
}

this.openItemDetail = function (arr, boxNo) {
	console.log("openItemDetail", arr, boxNo)
	//		
	root.openResetItemDetail();

	//			
	var mc = root.sc_game.mc_box_item_detail;
	if (boxNo === 1) {
		var krystalBox = root.sc_game['mc_krystal' + 0];
		krystalBox.mc_light_bg['tier' + arr[0].Tier].gotoAndPlay(0);
		krystalBox.gotoAndPlay('start');
		var box = mc.result_containter1.box0
		var iconBitmap = new createjs.Bitmap(arr[0].IconURL);
		var tier = (arr[0].Tier) - 1;

		box.mc_tier.gotoAndStop(tier);
		box.mc_tier.mc_icon_place.removeChildAt(0);
		box.mc_tier.mc_icon_place.addChild(iconBitmap);
		box.txt_cate.text = (arr[0].CategoryName).toUpperCase();
		box.txt_title.text = (arr[0].ItemName).toUpperCase();
		box.txt_desc.text = arr[0].Description1;
		//
		mc.btns.visible = true;
		mc.result_containter1.visible = true;
		mc.gotoAndPlay('start');
		root.sc_game.gotoAndPlay('start');
		root.autoMoveResultsDisable();
	} else if (boxNo >= 3) {
		selectId = 0
		mc.pageNumCont.visible = true;
		mc['pageClickCont' + ChestBoxSelectNo].visible = true;
		mc['result_containter' + ChestBoxSelectNo].visible = true;
		mc.arrows.visible = true;
		mc.btns.visible = true;
		var container = mc["result_containter" + ChestBoxSelectNo];
		for (var i in arr) {
			var krystalBox = root.sc_game['mc_krystal' + i];
			krystalBox.mc_light_bg['tier' + arr[i].Tier].gotoAndPlay(0);
			krystalBox.gotoAndPlay('start');
			mc["pageClickCont" + ChestBoxSelectNo]["btn_page_no" + i].gotoAndPlay('start');

			var box = mc['result_containter' + ChestBoxSelectNo]['box' + i]
			var iconBitmap = new createjs.Bitmap(arr[i].IconURL);
			var tier = (arr[i].Tier) - 1;
			box.mc_tier.gotoAndStop(tier);
			box.mc_tier.mc_icon_place.removeChildAt(0);
			box.mc_tier.mc_icon_place.addChild(iconBitmap);
			box.txt_cate.text = (arr[i].CategoryName).toUpperCase();
			box.txt_title.text = (arr[i].ItemName).toUpperCase();
			box.txt_desc.text = arr[i].Description1;
			//
			var pageClickMc = mc["pageClickCont" + ChestBoxSelectNo]["btn_page_no" + i];
			pageClickMc.removeAllEventListeners();
			//
			var hit = new createjs.Shape();
			hit.graphics.beginFill("#000").drawRect(0, 0, 10, 10);
			pageClickMc.hitArea = hit;
			pageClickMc.cursor = "pointer";
			pageClickMc.addEventListener("mouseover", function (event) {
				var flagLabel = "over";
				var id = event.currentTarget.name.substring(11);
				event.currentTarget.gotoAndPlay(flagLabel);
				trackDelta = 0;
			});

			pageClickMc.addEventListener("mouseout", function (event) {
				var flagLabel = "start";
				var id = event.currentTarget.name.substring(11);
				trackDelta = 0;
				if (Number(selectId) === Number(id)) {
					mc["pageClickCont" + ChestBoxSelectNo]["btn_page_no" + selectId].gotoAndPlay('select');
				} else {
					event.currentTarget.gotoAndPlay("start");
				}
			});

			pageClickMc.addEventListener("click", function (event) {
				var flagLabel = "select";
				var id = event.currentTarget.name.substring(11);
				if (selectId !== id) {
					selectId = id;
					trackDelta = 0;
					setResultArrows(container, id)
				} else {

				}
			});
		}
		mc.gotoAndPlay('start');
		root.sc_game.gotoAndPlay('start');
		//버튼 스크롤바
		mc.arrows.btn_left_arrow.removeAllEventListeners();
		mc.arrows.btn_right_arrow.removeAllEventListeners();
		mc.arrows.btn_left_arrow.addEventListener("click", function (event) {
			if (selectId < 1) {
				selectId = (ChestBoxSelectNo - 1);
			} else {
				selectId--
			}
			trackDelta = 0;
			setResultArrows(container, selectId)
		});
		mc.arrows.btn_right_arrow.addEventListener("click", function (event) {
			if (selectId > (ChestBoxSelectNo - 2)) {
				selectId = 0;
			} else {
				selectId++
			}
			trackDelta = 0;
			setResultArrows(container, selectId);
		});

		mc.pageNumCont.txt_no.text = (Number(selectId) + 1) + "/" + ChestBoxSelectNo;
		mc["pageClickCont" + ChestBoxSelectNo]["btn_page_no" + 0].gotoAndPlay('select');
		//	
		//
		root.autoMoveResultsEnable();
		//
	}


	mc.btns.btn_go_my_inven.addEventListener('click', function (e) {
		root.selectId = 0
		root.autoMoveResultsDisable()
		// 상단 버튼 disabled
		root.setMenuBlocker('off');
		//
		root.sc_game.mc_box_item_detail.gotoAndPlay(0)
		//
		root.goBackCheck(4);
		e.currentTarget.removeAllEventListeners();
	})
	mc.btns.btn_open_more.addEventListener('click', function (e) {
		root.selectId = 0
		root.autoMoveResultsDisable()
		// 상단 버튼 disabled
		root.setMenuBlocker('off');
		//
		root.sc_game.mc_box_item_detail.gotoAndPlay(0)
		//
		root.goBackCheck(2);
		e.currentTarget.removeAllEventListeners();
	})
}
function setResultArrows(mc, id) {
	var setValue = -((972 * id))
	createjs.Tween.get(mc, {
		override: true
	})
		.to({
			x: setValue
		}, 800, createjs.Ease.quintOut)
		.call(rightComplete);
}

function rightComplete() {
	var mc = root.sc_game.mc_box_item_detail;
	mc.pageNumCont.txt_no.text = (Number(selectId) + 1) + "/" + ChestBoxSelectNo;
	for (var i = 0; i < items.length; i++) {
		if (Number(selectId) !== Number(i)) {
			mc["pageClickCont" + ChestBoxSelectNo]["btn_page_no" + i].gotoAndPlay('start');
		} else {
			mc["pageClickCont" + ChestBoxSelectNo]["btn_page_no" + i].gotoAndPlay('select');
		}
	}
}

var swapInterval = 2;
var trackDelta = 0;

this.autoMoveResultsEnable = function () {
	createjs.Ticker.addEventListener("tick", onTick);
}
this.autoMoveResultsDisable = function () {
	trackDelta = 0;
	createjs.Ticker.removeEventListener("tick", onTick);
}

function onTick(e) {
	trackDelta += e.delta;
	if (trackDelta >= swapInterval * 1200) {
		console.log(e);
		if (selectId > (ChestBoxSelectNo - 2)) {
			selectId = 0;
		} else {
			selectId++
		}
		setResultArrows(root.sc_game.mc_box_item_detail["result_containter" + ChestBoxSelectNo], selectId)
		trackDelta = 0;
	}
}

this.stage.addEventListener("stagemousemove", resetDelta);
function resetDelta() {
	trackDelta = 0;
}
