this.buyKeyEventMc = function () {
	for (var i = 0; i < 4; i++) {
		var mc = root.sc_buy_key["mc_hammer_key" + i];
		mc.cursor = "pointer";
		var count = i;
		mc.mc_key_title.gotoAndStop(count);
		mc.mc_sale_tag.visible = false;

		if (i !== 3) {
			if (i === 0) {
				mc.btn_buy_key.txt_key_value.text = countArr[i].value + " HAMMER";
				mc.txt_price.text = countArr[i].price + " DKC / EA";
			} else {

				mc.btn_buy_key.txt_key_value.text = countArr[i].value + " HAMMERS";
				if (i === 1) {
					mc.mc_sale_tag.visible = true;
					mc.mc_sale_tag.txt_sale.text = DISCOUNT_05 + "% off";
					mc.txt_price.text = countArr[i].price + " DKC / EA";
				}
				if (i === 2) {
					mc.mc_sale_tag.visible = true;
					mc.mc_sale_tag.txt_sale.text = DISCOUNT_10 + "% off";
					mc.txt_price.text = countArr[i].price + " DKC / EA";
				}
			}
		} else {
			mc.mc_sale_tag.visible = true;
			mc.mc_sale_tag.txt_sale.text = DISCOUNT_10 + "% off";
			mc.txt_price.text = countArr[i].price + " DKC / EA";
			mc.btn_buy_key.txt_key_value.text = countArr[i].value;
		}

		mc.removeAllEventListeners();

		mc.addEventListener("mouseover", function (event) {
			createjs.Tween.get(event.currentTarget, {
				override: true
			}).to({
				y: 200
			}, 400, createjs.Ease.quintOut);
		});
		mc.addEventListener("mouseout", function (event) {
			createjs.Tween.get(event.currentTarget, {
				override: true
			}).to({
				y: 210
			}, 400, createjs.Ease.quintOut);
		});
		mc.addEventListener("click", function (event) {
			var id = Number(event.currentTarget.name.substring(13));
			if (id !== 3) {
				root.getBuyKeyClickNormal(id, countArr[id].value, countArr[id].price);
			} else {
				root.getBuyKeyClickChoose();
			}
			mc.removeAllEventListeners();
		});
	}
}
