//일반키 클릭
this.getBuyKeyClickNormal = function (id, keys, price) {
	var mc = root.sc_buy_key
	var buyBoxMc = mc.mc_puchased_box
	var balanceBox = buyBoxMc.mc_purchased_balance

	mc.gotoAndPlay('puchase');
	//
	buyBoxMc.mc_shop_charater.gotoAndPlay('start');
	//
	var count = id
	buyBoxMc.mc_key_title.gotoAndStop(count);
	buyBoxMc.txt_total_key.text = keys + 'x';
	buyBoxMc.txt_price_each.text = price + ' DKC / EA';
	//
	balanceBox.txt_balance.text = 'MY BALANCE'
	balanceBox.txt_balance_value.text = MY_TOTAL_BALANCE + ' DKC';
	balanceBox.txt_total_key.text = keys + 'x HAMMER(S)';
	var calcTotalBalance = "-" + (price * keys);
	balanceBox.txt_calc_value.text = calcTotalBalance + ' DKC';
	//	
	buyBoxMc.btn_buy_now.removeAllEventListeners();

	buyBoxMc.btn_buy_now.addEventListener("click", function (event) {
		//sending to loading				
		//response ok next
		buyBoxMc.mc_shop_charater.gotoAndPlay(0);
		mc.gotoAndPlay('loading');
		root.setGachaPurchaseKey(keys);
		buyBoxMc.btn_buy_now.removeAllEventListeners();
	});
}
//choose키 클릭
this.getBuyKeyClickChoose = function () {
	var mc = root.sc_buy_key;
	var boxChoice = mc.mc_buy_key_stepper;
	var keyValue = 11;
	mc.gotoAndPlay('stepper');

	boxChoice.mc_sale_tag.txt_sale.text = DISCOUNT_10 + '% off';
	boxChoice.txt_key_value.text = keyValue + 'x';
	boxChoice.txt_total_price.text = (KEY_PRICE - (KEY_PRICE * (DISCOUNT_10 / 100))) * keyValue;

	boxChoice.btn_step_up.removeAllEventListeners();
	boxChoice.btn_step_down.removeAllEventListeners();
	boxChoice.btn_step_get_key.removeAllEventListeners();

	boxChoice.btn_step_up.addEventListener("click", function (event) {
		if (keyValue < 1000) {
			keyValue++;
		}
		boxChoice.txt_key_value.text = keyValue + 'x';
		boxChoice.txt_total_price.text = (KEY_PRICE - (KEY_PRICE * (DISCOUNT_10 / 100))) * keyValue;
	});
	boxChoice.btn_step_down.addEventListener("click", function (event) {
		if (keyValue > 11) {
			keyValue--;
		} else {
			keyValue = 11;
		}
		boxChoice.txt_key_value.text = keyValue + 'x';
		boxChoice.txt_total_price.text = (KEY_PRICE - (KEY_PRICE * (DISCOUNT_10 / 100))) * keyValue;
	});

	boxChoice.btn_step_get_key.addEventListener("click", function (event) {
		boxChoice.btn_step_up.removeAllEventListeners();
		boxChoice.btn_step_down.removeAllEventListeners();
		boxChoice.btn_step_get_key.removeAllEventListeners();

		root.getBuyKeyClickNormal(3, keyValue, KEY_PRICE - (KEY_PRICE * (DISCOUNT_10 / 100)));
		//
	});
}

//purchased 완료
this.getBuyKeyDone = function (sentKeyValue, value) {
	var mc = root.sc_buy_key
	var doneBoxMc = mc.mc_puchased_done
	doneBoxMc.btn_get_more_key.removeAllEventListeners();
	doneBoxMc.btn_open_krystal.removeAllEventListeners();

	mc.gotoAndPlay('done');
	//
	if (sentKeyValue == 'success') {
		doneBoxMc.txt.text = 'ALRIGHT! YOU\'VE GOT ' + value + ' HAMMER(S).'
		if (GOT_KEY == 0) {
			root.mc_game_menu_bar.mc_menubar_ani.btn_get_key.txt.text = "BUY KEY";
		} else {
			root.mc_game_menu_bar.mc_menubar_ani.btn_get_key.txt.text = GOT_KEY;
		}
		doneBoxMc.btn_get_more_key.x = 214
		doneBoxMc.btn_get_more_key.y = 271
		doneBoxMc.btn_open_krystal.x = 379
		doneBoxMc.btn_open_krystal.y = 271
	} else {
		doneBoxMc.txt.text = 'UNFORUNTELY, YOU DID NOT GET ' + value + ' HAMMER(s). PLEASE TRY AGAIN'
		if (GOT_KEY == 0) {
			root.mc_game_menu_bar.mc_menubar_ani.btn_get_key.txt.text = "BUY KEY";
		} else {
			root.mc_game_menu_bar.mc_menubar_ani.btn_get_key.txt.text = GOT_KEY;
		}
		doneBoxMc.btn_get_more_key.x = 280
		doneBoxMc.btn_get_more_key.y = 271
		doneBoxMc.btn_open_krystal.x = 1000
		doneBoxMc.btn_open_krystal.y = 1000
	}
	doneBoxMc.btn_get_more_key.addEventListener("click", function (event) {
		root.goBackCheck(3)
		event.currentTarget.removeAllEventListeners();
	});

	doneBoxMc.btn_open_krystal.addEventListener("click", function (event) {
		root.goBackCheck(2)
		event.currentTarget.removeAllEventListeners();
	});
}

this.setGachaPurchaseKey = function (keyValue) {
	//this.mc_buy_key_title.txt.text = "WAITING FOR RESPONSE";		
	$.ajax({
		url: ioRootPath + "/secured/json.do?callback=?",
		type: 'GET',
		data: {
			cm: "Service.Gacha.PurhaseGachaKey_DK",
			serviceid: GachaUserInfo[0].ServiceID,
			keycount: keyValue,
			userip: UIP
		},
		crossDomain: true,
		dataType: 'json'
	})
		.success(function (res) {
			GachaUserInfo = [];
			var code = res.ResultCode[0].Code;
			console.log('Service.Gacha.PurhaseGachaKey_DK', res)
			if (code === "10000") {
				GachaUserInfo = res.GachaUserInfo;
				GOT_KEY = res.GachaUserInfo[0].KeyAmount;
				MY_TOTAL_BALANCE = res.GachaUserInfo[0].UWOBalance;
				//
				root.getBuyKeyDone('success', keyValue)
			} else {
				root.getBuyKeyDone('error', keyValue)
			}
		})
		.error(function (e) {
			console.log("error");
		})
}

